mendeley.map:
function(doc) {
  if (doc.doc_type === "current" && doc.source === "mendeley") {
    emit(doc.doi, {readers: doc.event_metrics.shares, groups: doc.event_metrics.groups, total: doc.event_metrics.total});
  }
  }

counter.map:
function(doc) {
  if (doc.doc_type === "current" && doc.source === "counter") {
    emit(doc.doi, {html: doc.event_metrics.html, pdf: doc.event_metrics.pdf, total: doc.event_metrics.total});
  }
  }

counter_html_views.map:
function(doc) {
  if (doc.doc_type === "current" && doc.source === "counter") {
    var views = {};
    var key = "";
    for (var i = 0; i < doc.events.length; i++) {
      key = doc.events[i].year + "-" + doc.events[i].month;
      views[key] = parseInt(doc.events[i].html_views);
    } 
    emit(doc.doi, views);
  } 
}

